mapper.update:
update t_blog
set `title` = #{title}, content=#{content}, description =#{description}, first_picture=#{firstPicture},create_time =#{createTime},
    update_time=#{updateTime},appreciation=#{appreciation},views=#{views},comment=#{comment},collection=#{collection},
    `commentable` = #{commentable}, original=#{original}, special_column_id=#{specialColumnId},user_id =#{userId},examine=#{examine}
where id = #{id}

update t_blog
set examine = #{st}
where id = #{id}

mapper.select:
select id,title
from t_blog
where id=#{id}

select *
from t_blog
where id = #{blogid} and user_id = #{userid}

select * from blogweb.t_blog
join blogweb.t_blog_column as bc on bc.blog_id = t_blog.id and bc.column_id = (select id from blogweb.t_column where name=#{column} and user_id = #{id})
join (select blog_id from t_blog_tag join (select id from t_tag where name = #{tag}) as t1 on t1.id = t_blog_tag.tag_id) as b2 on b2.blog_id = t_blog.id
and DATE_FORMAT(create_time,'%Y') = #{nian}
and DATE_FORMAT(create_time,'%m') = #{yue}
and examine = '草稿'
and examine = '审核中'
and (content like concat('%',#{key},'%') or title like concat('%',#{key},'%'))
and (original = true and examine = '已通过')
and (original = false and examine = '已通过')
and user_id = #{id}
limit ${offset}, ${pagesize}

select *
from blogweb.t_blog


        and DATE_FORMAT(create_time,'%Y') = #{nian}


        and DATE_FORMAT(create_time,'%m') = #{yue}


        and and DATE_FORMAT(fix_time,'%Y') = ${column}
